INSERT INTO public.recipes (id, title, description, created_at, updated_at, img_url, "time", instructions, url, author, ingredients_string) VALUES (10, 'My Chicken Korma', NULL, '2021-04-27 12:22:00.833523', '2021-04-27 12:22:00.833523', 'https://spoonacular.com/recipeImages/652834-556x370.jpg', 45, '<ol><li>Cut the chicken breasts into small pieces.</li><li>Heat Oil in a pan. Add the ginger/garlic paste to the oil and fry it for about 3 mins at a low heat. Then add cumin, coriander, almond and poppy seeds. Fry the masala for about 3-4 mins at a low heat.</li><li>Add the chicken pieces to the masala and continue to fry for another 5 mins at a low heat, stirring carefully and folding the masala from across the pan.</li><li>Add the coconut milk, stir the chicken pieces and ensure they are covered with coconut milk. Add salt to taste and the raisins.</li><li>Cover the pan and let the chicken cook with occasional stirring.</li><li>Serve with basmati rice.</li></ol>', 'http://www.foodista.com/recipe/DYMLK2QG/my-chicken-korma', 'Foodista.com – The Cooking Encyclopedia Everyone Can Edit', 'chicken breast
