INSERT INTO public.recipes (id, title, description, created_at, updated_at, img_url, "time", instructions, url, author, ingredients_string) VALUES (13, 'California Wild Rice & Beef Cabbage Wrap With Crunchy Ricotta Cheese', NULL, '2021-04-27 12:22:00.927043', '2021-04-27 12:22:00.927043', 'https://spoonacular.com/recipeImages/636812-556x370.jpg', 45, '<ol><li>Cook 1 cup California wild rice according to package directions, adding tomato paste, Italian seasoning, and 2 teaspoons salt.</li><li>In a large bowl, combine 1/2 gallon water and 2 cups ice, to make an ice bath.</li><li>Bring 1/2 gallon water and 2 teaspoons salt to a boil, adjust heat to make water simmer. Add cabbage leaves, and cook for 1 to 2 minutes, or until cabbage leaves are soft.</li><li>Remove cabbage from boiling water and place in ice bath until cabbage is cool. Drain cabbage leaves on paper towels.</li><li>Heat up a frying pan and saute ground beef and flavor with paprika, white pepper, ground red pepper, and 1/2 teaspoon salt. Cook over medium-high heat until beef is browned and crumbles.</li><li>Add wild rice mixture, stirring to combine, and set aside.</li><li>In a small bowl, combine ricotta cheese, grated carrot, sunflower seeds, and remaining 1/4 teaspoon salt.</li><li>Serve wild rice mixture in or wrapped with cabbage leaves with ricotta mixture on the side.</li></ol>', 'http://www.foodista.com/recipe/FY5R5PYS/california-wild-rice-beef-cabbage-wrap-with-crunchy-ricotta-cheese', 'Foodista.com – The Cooking Encyclopedia Everyone Can Edit', 'carrot
