INSERT INTO public.recipes (id, title, description, created_at, updated_at, img_url, "time", instructions, url, author, ingredients_string) VALUES (94, 'Chipotle Cheese Stuffed Black Bean Burgers with Avocado Creme', NULL, '2021-04-27 14:03:47.442715', '2021-04-27 14:03:47.442715', 'https://spoonacular.com/recipeImages/638744-556x370.jpg', 45, 'Add a swirl of olive oil to a skillet and add diced mushrooms and onions.
Saute until softened then season with salt and pepper.
Add drained black beans to a medium bowl and press and mash with a fork.
Mix in mushrooms and onions then add egg and bread crumbs.
