INSERT INTO public.recipes (id, title, description, created_at, updated_at, img_url, "time", instructions, url, author, ingredients_string) VALUES (106, 'Stuffed mushrooms and Chow Mein noodles', NULL, '2021-04-27 14:16:36.515909', '2021-04-27 14:16:36.515909', 'https://spoonacular.com/recipeImages/662054-556x370.jpg', 45, 'Blanch the spinach in a very little water in the microwave oven for 3 minutes on MAX. Drain and coarsely chop. Chop mushroom stems in a blender.
Melt the butter over low heat, add the onion and cook for 1 minute. Add chopped mushroom stems, and saut for 4-5 minutes, stirring often.
