INSERT INTO public.recipes (id, title, description, created_at, updated_at, img_url, "time", instructions, url, author, ingredients_string) VALUES (119, 'Brined Chicken Breast with Sautéed Onion Dipping Sauce', NULL, '2021-04-27 14:16:37.798257', '2021-04-27 14:16:37.798257', 'https://spoonacular.com/recipeImages/636118-556x370.jpg', 45, '<ol><li>For the Dipping Sauce:</li><li>Saut the onion in a tablespoon of olive oil until transparent.  Let the onion cool.</li><li>Place the onion in a food processor and add the rest of the ingredients.  Give a few good chops until the desired consistency is reached.  Place in a container and refrigerate.</li><li>For the Chicken Breast:</li><li>Combine all of the above in a pot; add the chicken and cover in the refrigerator for a few hours or overnight.</li><li>Remove from the brine and cut the chicken into desired sized pieces.</li><li>Heat a 10 inch frying pan with 2 tablespoons of olive oil.</li><li>If the chicken is cut into  to  inch thickness it should take about 6 to 10 minutes over medium heat; turning the chicken pieces occasionally to saut evenly.  Do not overcrowd the pan.</li><li>Serve with the onion dipping sauce.</li></ol>', 'http://www.foodista.com/recipe/HNZLCVCN/brined-chicken-breast-with-saut-ed-onion-dipping-sauce', 'Foodista.com – The Cooking Encyclopedia Everyone Can Edit', 'bell pepper
