INSERT INTO public.recipes (id, title, description, created_at, updated_at, img_url, "time", instructions, url, author, ingredients_string) VALUES (138, 'Cider-Braised Pork Shoulder With Caramelized Onion and Apple Confit', NULL, '2021-04-27 14:16:39.525162', '2021-04-27 14:16:39.525162', 'https://spoonacular.com/recipeImages/639401-556x370.jpg', 45, 'Preheat oven to 400 F.
