INSERT INTO public.recipes (id, title, description, created_at, updated_at, img_url, "time", instructions, url, author, ingredients_string) VALUES (218, 'Parmesan Mashed Potatoes', NULL, '2021-04-27 14:29:20.460485', '2021-04-27 14:29:20.460485', 'https://spoonacular.com/recipeImages/654679-556x370.jpg', 45, '<ol><li>Place potatoes in a medium pot and cover with cold water. Bring to a boil, heat to a simmer. Add a generous pinch of salt and continue to simmer until potatoes are fork tender, about 20 minutes. Heat cream, salt and pepper and butter in a medium saucepan over medium heat until steaming hot, about 6 minutes.</li><li>When potatoes are done, drain and return potatoes to pot to dry slightly. Optional: Run potatoes through food mill or potato ricer into pot.</li><li>Add potatoes to the cream and butter mixture. Stir in parmesan cheese, taste for seasoning.</li></ol>', 'http://www.foodista.com/recipe/WRQP2BQF/parmesan-mashed-potatoes', 'Foodista.com – The Cooking Encyclopedia Everyone Can Edit', 'butter
