INSERT INTO public.recipes (id, title, description, created_at, updated_at, img_url, "time", instructions, url, author, ingredients_string) VALUES (227, 'Broccolini Quinoa Pilaf', NULL, '2021-04-27 14:29:20.57275', '2021-04-27 14:29:20.57275', 'https://spoonacular.com/recipeImages/715769-556x370.jpg', 30, '<ol><li>In a large pan with lid heat olive oil over medium high heat. Add onions and cook for 1 minute. Add garlic and cook until onions are translucent and garlic is fragrant.</li><li>Add quinoa to pan, stir to combine. Slowly add in broth and bring to a boil.</li><li>Cover and reduce heat to low, cook for 15 minutes.</li><li>In the last 2-3 minutes of cooking add in broccolini on top of the quinoa (do not stir) and cover.</li><li>Uncover and toss broccolini and quinoa together.</li><li>Season to taste with salt and pepper.</li><li>Add walnuts and serve hot.</li></ol>', 'http://pickfreshfoods.com/broccolini-quinoa-pilaf/', 'Pick Fresh Foods', 'broccolini
