INSERT INTO public.recipes (id, title, description, created_at, updated_at, img_url, "time", instructions, url, author, ingredients_string) VALUES (274, 'Grilled Eggplant and Heirloom Tomato Stacks With Basil and Tomato Coulis', NULL, '2021-04-27 14:29:21.461829', '2021-04-27 14:29:21.461829', 'https://spoonacular.com/recipeImages/645696-556x370.jpg', 45, '<ol><li>Prepare Tomato Coulis:</li><li>Make shallow incisions with a paring knife around the tomato stems and scoop out the stems.</li><li>Make a shallow X-incision in bottom of tomatoes.</li><li>Bring a large saucepan of water to a rolling boil.</li><li>Plunge tomatoes into water for 10 seconds.</li><li>Remove and submerge tomatoes in bowl of ice water.</li><li>Remove the cooled tomatoes from the water.</li><li>Peel away skin.</li><li>Remove seeds and coarsely chop tomatoes.</li><li>Combine tomatoes, garlic and olive oil in bowl of food processor.</li><li>Process until smooth.</li><li>Add salt and pepper.</li><li>Let Tomato Coulis stand at room temperature for one hour before serving.</li><li>Prepare Eggplant:</li><li>Preheat oven broiler or prepare grill.</li><li>Lightly brush eggplant slices on both sides with olive oil.</li><li>Sprinkle with salt and pepper.</li><li>Arrange on baking tray and grill in oven, turning once, until golden brown, about 3 minutes per side.</li><li>Or grill on barbecue, turning, until golden brown and cooked through.</li><li>Arrange Stacks:</li><li>Combine garlic and goat cheese in a small bowl.</li><li>Add salt and freshly ground black pepper to taste.</li><li>Arrange 1/2 of the eggplant slices on platter.</li><li>Spoon 2 teaspoons goat cheese over.</li><li>Top with 1 basil leaf.</li><li>Place second eggplant slice on basil leaf.</li><li>Top with one teaspoon goat cheese and basil leaf.</li><li>Lightly drizzle 2-3 teaspoons Tomato Coulis over and around the eggplant stack.</li><li>Garnish with one teaspoon grated Pecorino Romano cheese.</li><li>Serve immediately.</li></ol>', 'http://www.foodista.com/recipe/WXQZBFHK/grilled-eggplant-and-heirloom-tomato-stacks-with-basil-and-tomato-coulis', 'Foodista.com – The Cooking Encyclopedia Everyone Can Edit', 'basil leaves
