INSERT INTO public.recipes (id, title, description, created_at, updated_at, img_url, "time", instructions, url, author, ingredients_string) VALUES (364, 'Farro & Leek Soup', NULL, '2021-04-27 14:43:20.751799', '2021-04-27 14:43:20.751799', 'https://spoonacular.com/recipeImages/642597-556x370.jpg', 45, 'In a heavy bottomed pot, heat olive oil on med/low heat. Add in the vegetables and sweat until translucent and soft WITHOUT browning. If the vegetables stick to the pan, lower the heat & add a spoonful of water.
Then toss in the farro, toasting with the vegetables for 1-2 minutes. Add in the herbs & stock. Season with salt & pepper.
