INSERT INTO public.recipes (id, title, description, created_at, updated_at, img_url, "time", instructions, url, author, ingredients_string) VALUES (485, 'Pecan Pumpkin Pie Dessert Pizza with Maple Whipped Cream', NULL, '2021-04-27 14:47:11.186463', '2021-04-27 14:47:11.186463', 'https://spoonacular.com/recipeImages/655525-556x370.jpg', 45, '<ol><li>Preheat oven to 425 degrees.</li><li>Melt 1/2 of the butter and 1 tbsp brown sugar in a small skillet.</li><li>Brush onto on side of pizza crust.</li><li>Place on a baking sheet and bake for 3 minutes.</li><li>Meanwhile, add the rest of butter and 1 tbsp brown sugar to the skillet again.</li><li>Once it is melted add pecans and stir.</li><li>Cook for 2-3 minutes over medium until pecans are toasted and coated in sugar. Remove from heat.</li><li>Take crust out of oven and spread pumpkin pie filling evenly on top.</li><li>Sprinkle with pumpkin pie spice.</li><li>Remove pecans from butter mixture and sprinkle evenly on top of pizza.</li><li>Bake for 7 more minutes.</li><li>For Maple Whipped Cream: Using a cold bowl and beaters (I put mine in the freezer for about 30 minutes before using) beat heavy cream and maple syrup on high until stiff peaks form.</li><li>Remove pizza from oven. Cut into 4 pieces. Top with a dollop or two of whipped cream and serve!</li></ol>', 'http://www.foodista.com/recipe/YBRFWHCH/pecan-pumpkin-pie-dessert-pizza-with-maple-whipped-cream', 'Foodista.com – The Cooking Encyclopedia Everyone Can Edit', 'brown sugar
