INSERT INTO public.recipes (id, title, description, created_at, updated_at, img_url, "time", instructions, url, author, ingredients_string) VALUES (493, 'Lamb and Fresh Goat Cheese Roulade', NULL, '2021-04-27 14:47:12.09014', '2021-04-27 14:47:12.09014', 'https://spoonacular.com/recipeImages/649183-556x370.jpg', 45, 'Heat 2 tbsp. olive oil in a saut pan over medium high heat.
Add the garlic and saut for 10 seconds then add the spinach and saut only until the spinach wilts.
Set aside to cool.
Lay out the lamb loin and butterfly to create a flat piece about 6 inches wide.
Season the loin with salt and pepper.
