INSERT INTO public.recipes (id, title, description, created_at, updated_at, img_url, "time", instructions, url, author, ingredients_string) VALUES (572, 'Moroccan kofte and sausage stew', NULL, '2021-04-27 14:55:27.879813', '2021-04-27 14:55:27.879813', NULL, 45, 'Mix the beef, onion, chilli, spices and the chopped coriander leaves.
Add the egg and salt, mix and combine.
