INSERT INTO public.recipes (id, title, description, created_at, updated_at, img_url, "time", instructions, url, author, ingredients_string) VALUES (633, 'Beans Hawaiian', NULL, '2021-04-27 14:55:43.523281', '2021-04-27 14:55:43.523281', 'https://spoonacular.com/recipeImages/634545-556x370.jpg', 45, 'Soak dried navy beans overnight in a large pot. You want the water to be cold and about 2-3 inches above the beans. Cover and refrigerate at least 8 hours.
