INSERT INTO public.recipes (id, title, description, created_at, updated_at, img_url, "time", instructions, url, author, ingredients_string) VALUES (660, 'Frittata with Mushrooms, Thyme, and Parmigiano Cheese', NULL, '2021-04-27 14:55:53.575516', '2021-04-27 14:55:53.575516', 'https://spoonacular.com/recipeImages/643859-556x370.jpg', 45, '<ol><li>Pre-heat oven on to 350 degrees (later you''ll change it to "Broil").</li><li>Heat an 8-inch ovenproof skillet over medium-high heat.</li><li>Coat pan with cooking spray or butter.</li><li>Add mushrooms and green onions to pan; saut 12 minutes or until mushrooms are nicely browned.</li><li>In a medium bowl, add the eggs and egg replacement.</li><li>Add the half and half.</li><li>Add the Parmesan cheese.</li><li>Add the thyme and any additional herbs that you like.</li><li>Add salt and pepper and whisk everything together.</li><li>Pour the egg/cheese mixture into the same pan with the sauteed mushrooms and green onions.</li><li>Heat pan over medium heat.</li><li>Cook, covered, for about 6 minutes or when it is just a little bit liquid-y/runny on the top.</li><li>At that point, place the pan into the oven and cook on 350 for another 5 - 10 minutes.</li><li>Once the frittata is completely set and cooked (not runny on top) change the oven temperature to "Broil".</li><li>Place the pan on the lowest rack of the oven and broil for about 2 minutes or until a golden brown color forms on the top.</li></ol>', 'http://www.foodista.com/recipe/ZPHDGMDB/frittata-with-mushrooms-thyme-and-parmigiano-cheese', 'Foodista.com – The Cooking Encyclopedia Everyone Can Edit', 'egg replacement
