INSERT INTO public.recipes (id, title, description, created_at, updated_at, img_url, "time", instructions, url, author, ingredients_string) VALUES (715, 'Curried Cabbage & Kale Gratin', NULL, '2021-04-27 14:56:19.704503', '2021-04-27 14:56:19.704503', 'https://spoonacular.com/recipeImages/641060-556x370.jpg', 45, 'Heat the cooking fat in a large deep skillet or a Dutch oven, over medium heat
