INSERT INTO public.recipes (id, title, description, created_at, updated_at, img_url, "time", instructions, url, author, ingredients_string) VALUES (740, 'Powdered Buttermilk Beignets With Raspberry and Chocolate Dipping Sauce', NULL, '2021-04-27 14:56:46.980764', '2021-04-27 14:56:46.980764', 'https://spoonacular.com/recipeImages/657058-556x370.jpg', 45, '<ol><li>Raspberry Sauce:</li><li>In a saucepan, add the frozen raspberries and powdered sugar.</li><li>Bring to a simmer and cook for about 3-4 minutes.</li><li>Gently mash the raspberries with the back of a spatula while they cook.</li><li>After 4 minutes, remove from heat and add the raspberry jam.  You could puree this mixture before serving, but I prefer it chunky.</li><li>Chocolate Sauce:</li><li>In a saucepan, add the chocolate chips and cream.</li><li>Allow the chocolate chips to melt, stirring occasionally.</li><li>Remove from heat once chips have melted and add the golden syrup.</li><li>Beignets:</li><li>Heat the 1% milk in a saucepan.</li><li>Once it starts to simmer, remove pan from heat and add the buttermilk.</li><li>Pour milk into a standing mixer.</li><li>To the milk, whisk in the yeast and sugar.  Let sit until foamy, approximately 5 minutes.</li><li>In a separate bowl, mix the flours, baking soda and salt.</li><li>Turn the mixer on low speed and slowly add the flour mixture until just incorporated and dough is in the shape of a ball and is slightly sticky.</li><li>Cover the standing mixer bowl with saran wrap and let sit in a warm, draft-free location for about an hour.</li><li>After an hour, lightly flour your work surface and pour out your dough.</li><li>Using as little flour as possible, fold the dough over in half and tuck in the edges to make a ball.</li><li>Flatten the ball and with a floured rolling pin.</li><li>Roll the ball out into a circle until the circle is about  an inch thick.</li><li>Using a knife or other sharp object cut the dough into any shape you want.  I find that cutting the dough into long and thick logs makes the beignets good for dipping.</li><li>Let sit for a few minutes.</li><li>In the meantime, pour canola oil into a Dutch oven until the oil is about 2-3 inches deep.</li><li>Heat oil to 375 degrees.</li><li>Once oil is hot, drop in the dough  4-5 at a time and turn frequently.</li><li>Allow to fry for 2-3 minutes, or until a lovely golden dark brown.</li><li>Transfer the beignets to a drain on a paper towel lined plate.</li><li>Add some powdered sugar to the brown paper bag and drop in 3-4 beignets.</li><li>Close the bag and shake until the beignets are completely covered in powdered sugar.</li><li>Serve warm with the dipping sauces.</li></ol>', 'http://www.foodista.com/recipe/F8B6XBB4/powdered-buttermilk-beignets-with-raspberry-and-chocolate-dipping-sauce', 'Foodista.com – The Cooking Encyclopedia Everyone Can Edit', 'active yeast
