INSERT INTO public.recipes (id, title, description, created_at, updated_at, img_url, "time", instructions, url, author, ingredients_string) VALUES (2, 'Vegan Dirty Chai Pudding', NULL, '2021-04-27 12:20:59.485342', '2021-04-27 12:20:59.485342', 'https://spoonacular.com/recipeImages/664429-556x370.jpg', 45, 'Scoop the avocado into a food processor or blender. Measure in the agave nectar, cocoa powder, almond milk, vanilla and sea salt. Pulse and blend until silky smooth, scraping down sides as needed. Scrape into a large bowl and stir in the espresso grounds and chai spice mix. Taste and adjust espresso and chai flavoring as desired.
