INSERT INTO public.recipes (id, title, description, created_at, updated_at, img_url, "time", instructions, url, author, ingredients_string) VALUES (89, 'Watermelon Feta and Mint Salad', NULL, '2021-04-27 14:03:24.835892', '2021-04-27 14:03:24.835892', 'https://spoonacular.com/recipeImages/665012-556x370.jpg', 45, 'Directions: Cut watermelon in half and remove watermelon from rind.  You can either spoon it out like a mad man, carefully remove it and cut it into squares, or scoop it all out with a melon baller-which is what I did.  I liked the look of all those succelent balls of watermelon.
