INSERT INTO public.recipes (id, title, description, created_at, updated_at, img_url, "time", instructions, url, author, ingredients_string) VALUES (105, 'Vegetarian Mushroom Shepherd''s Pie', NULL, '2021-04-27 14:16:36.498917', '2021-04-27 14:16:36.498917', 'https://spoonacular.com/recipeImages/664680-556x370.jpg', 45, 'Peel potatoes and put them in a large pot filled with water. Boil them until they''re tender. Mash them and let them cool for a while.
