INSERT INTO public.recipes (id, title, description, created_at, updated_at, img_url, "time", instructions, url, author, ingredients_string) VALUES (115, 'Cheesy Cauliflower', NULL, '2021-04-27 14:16:37.475144', '2021-04-27 14:16:37.475144', 'https://spoonacular.com/recipeImages/637638-556x370.jpg', 45, '<ol><li>Preheat oven to 400</li><li>Trim cauliflower florets from the stalk. Discard stalk. Cut florets into 1-2 ince pieces. Steam the cauliflower for about 10 minutes until firm but tender.</li><li>Spread the florets out onto a paper towel to dry while making the cheese sauce</li><li>In a medium sauce pan, melt the butter over medium high heat, add the flour and whisk to combine. Cook for about 1-2 minutes to get rid of the flour taste.</li><li>Add the mustard powder, pinch of cayenne, black pepper and stir to combine.</li><li>Drizzle the milk in a steady stream whisking the whole time so that the mixture stays creamy.</li><li>Season with salt and bring to a simmer, stirring constantly.</li><li>When mixture thickens, add the cheese one handful at a time. Taste the sauce and adjust the salt/pepper as needed.</li><li>Spread the cauliflower florets into a 2 quart baking dish (or iron skillet), spoon the sauce over the top and sprinkle about 2 tbs of cheese over the top. Bake until browned and bubbly  about 30 minutes. Garnish with fresh herbs if desired.</li></ol>', 'http://www.foodista.com/recipe/DY78S8G3/cheesy-cauliflower', 'Foodista.com – The Cooking Encyclopedia Everyone Can Edit', 'all purpose flour
