INSERT INTO public.recipes (id, title, description, created_at, updated_at, img_url, "time", instructions, url, author, ingredients_string) VALUES (282, 'Healthy Vegan Red Velvet Brownies', NULL, '2021-04-27 14:42:05.681912', '2021-04-27 14:42:05.681912', 'https://spoonacular.com/recipeImages/646524-556x370.jpg', 45, '<ol><li>Preheat oven to 350 F.</li><li>Spray an 8x8 inch pan with cooking spray. Set aside.</li><li>In a food processor, puree kidney beans and beets. Transfer to a large bowl.</li><li>Add remaining ingredients and mix by hand until smooth and fully combined.</li><li>Fold batter into the greased pan.</li><li>Bake for 35-40 minutes, turning the pan halfway through.</li><li>When the brownies are done, test them with a toothpick. You want some batter to cling to the toothpick. Do not over bake.</li><li>Let cool completely. Refrigerate for one hour.</li><li>Cut into squares or use cookie cutters to make heart shapes.</li></ol>', 'http://www.foodista.com/recipe/YGBD7J2L/healthy-vegan-red-velvet-brownies', 'Foodista.com – The Cooking Encyclopedia Everyone Can Edit', 'almond extract
