INSERT INTO public.recipes (id, title, description, created_at, updated_at, img_url, "time", instructions, url, author, ingredients_string) VALUES (409, 'Cornmeal-Crusted Catfish with Cajun Seasoning and Spicy Tartar Sauce', NULL, '2021-04-27 14:45:25.769011', '2021-04-27 14:45:25.769011', 'https://spoonacular.com/recipeImages/640166-556x370.jpg', 45, 'Whisk the egg and milk in a medium bowl. Combine the cornmeal, chile powder, cayenne powder, paprika, and garlic in a large zip-top bag.
Dip each catfish filet in the egg mixture. Let excess egg drip off and place the filets in the bag with the cornmeal mixture. Zip the top and shake the bag until all pieces are well coated.
