INSERT INTO public.recipes (id, title, description, created_at, updated_at, img_url, "time", instructions, url, author, ingredients_string) VALUES (636, 'French Toast Croissants with Strawberries', NULL, '2021-04-27 14:55:43.919102', '2021-04-27 14:55:43.919102', 'https://spoonacular.com/recipeImages/643405-556x370.jpg', 20, '<ol><li>In small mixer bowl beat together all orange butter ingredients at medium speed, scraping bowl often, until light and fluffy (1 to 2 minutes); set aside.</li><li>In pie pan stir together milk, eggs and 1 teaspoon orange peel.</li><li>In 10 inch skillet or griddle melt 2 tablespoons butter over medium heat.</li><li>Dip 4 croissant halves into egg mixture, turning to coat both sides.</li><li>Place croissant halves, cut side down, in skillet. Cook, turning once, until golden brown (3 to 4 minutes on each side). Remove to serving platter; keep warm.</li><li>Repeat with remaining 2 tablespoons butter and 4 croissant halves. Dust croissant halves with powdered sugar; serve with orange butter.</li></ol>', 'http://www.foodista.com/recipe/F3MF6QN4/french-toast-croissants', 'Foodista.com – The Cooking Encyclopedia Everyone Can Edit', 'butter
