INSERT INTO public.recipes (id, title, description, created_at, updated_at, img_url, "time", instructions, url, author, ingredients_string) VALUES (605, 'Panna Cotta with Raspberry and Orange Sauce', NULL, '2021-04-27 14:55:32.530189', '2021-04-27 14:55:32.530189', 'https://spoonacular.com/recipeImages/654571-556x370.jpg', 45, 'Spray 6 ramekins or juice glasses with the nonstick cooking spray.
Add the half and half to a saucepan over low-medium heat (dont let the mixture get too hot or come to a boil).
When warm, sprinkle the gelatin over the mixture, and stir it to dissolve it.
Add the mascarpone and stir until melted.
